import React, { useState, useEffect } from 'react';
import RecordList from '../components/RecordList';
import { fetchRecords } from '../services/api';
import { Record } from '../types';

// 定义 HomePage 组件
const HomePage: React.FC = () => {
    // 定义状态变量 searchTerm，用于存储搜索关键字
    const [searchTerm, setSearchTerm] = useState('');
    // 定义状态变量 records，用于存储病例数据
    const [records, setRecords] = useState<Record[]>([]);

    // 使用 useEffect 钩子在组件加载时获取病例数据
    useEffect(() => {
        // 定义异步函数以加载病例数据
        const loadRecords = async () => {
            const fetchedRecords = await fetchRecords(); // 调用 API 获取病例数据
            setRecords(fetchedRecords); // 将获取到的数据存储到状态变量 records 中
        };
        loadRecords(); // 调用加载数据的函数
    }, []); // 空依赖数组表示仅在组件挂载时执行

    // 处理搜索输入框的变化
    const handleSearchChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        setSearchTerm(event.target.value); // 更新搜索关键字状态
    };

    // 根据搜索关键字过滤病例数据
    const filteredRecords = records.filter((record: Record) =>
        record.caseName.toLowerCase().includes(searchTerm.toLowerCase()) // 根据病例名称过滤
    );

    return (
        <div>
            {/* 页面标题 */}
            <h1>病例记录助手</h1>

            {/* 搜索输入框，用于输入搜索关键字 */}
            <input
                type="text"
                placeholder="搜索病例" // 提示用户输入搜索内容
                value={searchTerm} // 绑定输入框的值到 searchTerm 状态
                onChange={handleSearchChange} // 更新搜索关键字状态
            />

            {/* 按钮用于导航到新增病例页面 */}
            <button onClick={() => {/* Navigate to PatientPage for adding new record */}}>
                新增病例
            </button>

            {/* 显示过滤后的病例列表 */}
            <RecordList records={filteredRecords} />
        </div>
    );
};

export default HomePage;
