import React, { useState } from 'react'; // 导入 React 和 useState，用于创建组件和管理状态

// 定义一个函数式组件 PatientForm，接收两个回调函数 onSave 和 onCancel 作为 props
const PatientForm: React.FC<{ onSave: (data: any) => void; onCancel: () => void }> = ({ onSave, onCancel }) => {
    // 定义一个状态变量 formData，用于存储表单数据，初始值为一个包含所有字段的空对象
    const [formData, setFormData] = useState({
        caseName: '', // 病例名称
        symptomName: '', // 病症名称
        contact: '', // 联系方式
        gender: '', // 性别
        age: '', // 年龄
        diagnosisRecord: '' // 诊断记录
    });

    // 定义一个事件处理函数 handleChange，用于处理表单输入框的值变化
    const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
        const { name, value } = e.target; // 从事件对象中解构出输入框的名称和值
        // 更新状态，将对应字段的值更新为用户输入的值
        setFormData({ ...formData, [name]: value });
    };

    // 定义一个事件处理函数 handleSubmit，用于处理表单提交事件
    const handleSubmit = (e: React.FormEvent) => {
        e.preventDefault(); // 阻止表单的默认提交行为
        onSave(formData); // 调用父组件传递的 onSave 回调函数，并将表单数据作为参数传递
    };

    // 返回表单的 JSX 结构
    return (
        <form onSubmit={handleSubmit}> {/* 表单提交时触发 handleSubmit 函数 */}
            <div>
                <label>病例名称:</label> {/* 病例名称的标签 */}
                <input type="text" name="caseName" value={formData.caseName} onChange={handleChange} required /> {/* 输入框，绑定值和事件处理函数 */}
            </div>
            <div>
                <label>病症名称:</label> {/* 病症名称的标签 */}
                <input type="text" name="symptomName" value={formData.symptomName} onChange={handleChange} required /> {/* 输入框，绑定值和事件处理函数 */}
            </div>
            <div>
                <label>联系方式:</label> {/* 联系方式的标签 */}
                <input type="text" name="contact" value={formData.contact} onChange={handleChange} required /> {/* 输入框，绑定值和事件处理函数 */}
            </div>
            <div>
                <label>性别:</label> {/* 性别的标签 */}
                <input type="text" name="gender" value={formData.gender} onChange={handleChange} required /> {/* 输入框，绑定值和事件处理函数 */}
            </div>
            <div>
                <label>年龄:</label> {/* 年龄的标签 */}
                <input type="number" name="age" value={formData.age} onChange={handleChange} required /> {/* 数字输入框，绑定值和事件处理函数 */}
            </div>
            <div>
                <label>诊断记录:</label> {/* 诊断记录的标签 */}
                <textarea name="diagnosisRecord" value={formData.diagnosisRecord} onChange={handleChange} required /> {/* 文本区域，绑定值和事件处理函数 */}
            </div>
            <div>
                <button type="submit">保存</button> {/* 提交按钮，触发表单提交事件 */}
                <button type="button" onClick={onCancel}>取消</button> {/* 取消按钮，触发 onCancel 回调函数 */}
            </div>
        </form>
    );
};

export default PatientForm; // 导出组件，供其他模块使用
