import React, { useEffect, useState } from 'react';
import { fetchRecords } from '../services/api';

// 定义病例数据的类型
interface Record {
    id: string;
    caseName: string;
    symptom: string;
    contact: string;
    gender: string;
    age: number;
    lastDiagnosisDate: string;
}

// 定义 RecordList 组件
const RecordList: React.FC = () => {
    // 修改状态变量的类型
    const [records, setRecords] = useState<Record[]>([]);
    // 定义状态变量 searchTerm，用于存储搜索关键字
    const [searchTerm, setSearchTerm] = useState('');

    // 使用 useEffect 钩子在组件加载时获取病例数据
    useEffect(() => {
        // 定义异步函数以获取病例数据
        const getRecords = async () => {
            const data = await fetchRecords(); // 调用 API 获取病例数据
            setRecords(data); // 将获取到的数据存储到状态变量 records 中
        };
        getRecords(); // 调用获取数据的函数
    }, []); // 空依赖数组表示仅在组件挂载时执行

    // 根据搜索关键字过滤病例数据
    const filteredRecords = records.filter(record =>
        record.caseName.toLowerCase().includes(searchTerm.toLowerCase()) || // 根据病例名称过滤
        record.symptom.toLowerCase().includes(searchTerm.toLowerCase()) // 根据病症过滤
    );

    return (
        <div>
            {/* 搜索输入框，用于输入搜索关键字 */}
            <input
                type="text"
                placeholder="搜索病例..." // 提示用户输入搜索内容
                value={searchTerm} // 绑定输入框的值到 searchTerm 状态
                onChange={(e) => setSearchTerm(e.target.value)} // 更新搜索关键字状态
            />
            {/* 显示过滤后的病例列表 */}
            <ul>
                {filteredRecords.map(record => (
                    <li key={record.id}> {/* 使用病例的唯一 ID 作为列表项的 key */}
                        <h3>{record.caseName}</h3> {/* 显示病例名称 */}
                        <p>病症: {record.symptom}</p> {/* 显示病症 */}
                        <p>联系方式: {record.contact}</p> {/* 显示联系方式 */}
                        <p>性别: {record.gender}</p> {/* 显示性别 */}
                        <p>年龄: {record.age}</p> {/* 显示年龄 */}
                        <p>最后诊断时间: {record.lastDiagnosisDate}</p> {/* 显示最后诊断时间 */}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default RecordList;
