import axios from 'axios';
import { Record } from '../types';

// 定义 API 的基础 URL，通常是后端服务的地址
const API_BASE_URL = 'http://localhost:5000/api'; // 替换为实际的 API 基础 URL

// 获取所有记录的函数
export const fetchRecords = async (): Promise<Record[]> => {
    try {
        // 发送 GET 请求到 /records 端点以获取记录数据
        const response = await axios.get(`${API_BASE_URL}/records`);
        // 返回从服务器获取的数据
        return response.data;
    } catch (error) {
        // 如果请求失败，抛出错误并附带错误信息
        throw new Error('获取记录时出错: ' + (error as Error).message);
    }
};

// 根据 ID 获取记录的函数
export const fetchRecordById = async (id: string): Promise<Record> => {
    try {
        // 发送 GET 请求到 /records/{id} 端点以获取指定 ID 的记录
        const response = await axios.get(`${API_BASE_URL}/records/${id}`);
        // 返回从服务器获取的数据
        return response.data;
    } catch (error) {
        // 如果请求失败，抛出错误并附带错误信息
        throw new Error('获取记录时出错: ' + (error as Error).message);
    }
};

// 保存新记录的函数
export const saveRecord = async (record: Record): Promise<Record> => {
    try {
        // 发送 POST 请求到 /records 端点以保存新记录
        // record 参数是要保存的记录数据
        const response = await axios.post(`${API_BASE_URL}/records`, record);
        // 返回从服务器获取的响应数据
        return response.data;
    } catch (error) {
        // 如果请求失败，抛出错误并附带错误信息
        throw new Error('保存记录时出错: ' + (error as Error).message);
    }
};

// 更新现有记录的函数
export const updateRecord = async (id: string, updatedRecord: Partial<Record>): Promise<Record> => {
    try {
        // 发送 PUT 请求到 /records/{id} 端点以更新指定 ID 的记录
        // id 参数是记录的唯一标识符，updatedRecord 是更新后的记录数据
        const response = await axios.put(`${API_BASE_URL}/records/${id}`, updatedRecord);
        // 返回从服务器获取的响应数据
        return response.data;
    } catch (error) {
        // 如果请求失败，抛出错误并附带错误信息
        throw new Error('更新记录时出错: ' + (error as Error).message);
    }
};
