import axios, { AxiosError, InternalAxiosRequestConfig } from 'axios';
import { Record } from '../types';

// 扩展AxiosRequestConfig类型
declare module 'axios' {
    export interface InternalAxiosRequestConfig {
        retry?: boolean;
        retryCount?: number;
    }
}

// 定义API基础URL
const API_BASE_URL = 'http://localhost:5000/api';

// 创建axios实例
const api = axios.create({
    baseURL: API_BASE_URL,
    timeout: 10000, // 10秒超时
    headers: {
        'Content-Type': 'application/json'
    }
});

// 添加请求重试拦截器
api.interceptors.response.use(undefined, async (error: AxiosError) => {
    const config = error.config;
    if (!config || !config.retry) {
        return Promise.reject(error);
    }

    // 确保retryCount有默认值
    if (typeof config.retryCount === 'undefined') {
        config.retryCount = 0;
    }

    if (config.retryCount >= 3) {
        return Promise.reject(error);
    }

    config.retryCount += 1;
    const backoff = new Promise(resolve => {
        setTimeout(() => resolve(null), 1000 * Math.pow(2, config.retryCount - 1));
    });
    await backoff;
    return api(config);
});

// 定义错误响应的接口
interface ErrorResponse {
    error: string;
}

// 错误处理函数
const handleError = (error: AxiosError) => {
    if (error.response) {
        const errorData = error.response.data as ErrorResponse;
        throw new Error(errorData.error || '请求失败');
    } else if (error.request) {
        throw new Error('无法连接到服务器');
    } else {
        throw new Error('请求配置错误');
    }
};

// 重试函数
const retryRequest = async (fn: () => Promise<any>, retries = 3, delay = 1000): Promise<any> => {
    try {
        return await fn();
    } catch (error) {
        if (retries > 0) {
            await new Promise(resolve => setTimeout(resolve, delay));
            return retryRequest(fn, retries - 1, delay * 2);
        }
        throw error;
    }
};

// 获取所有记录
export const fetchRecords = async (): Promise<Record[]> => {
    return retryRequest(async () => {
        try {
            const response = await api.get('/records');
            return response.data;
        } catch (error) {
            if (axios.isAxiosError(error)) {
                if (!error.response) {
                    throw new Error('无法连接到服务器，请检查服务器是否启动');
                }
                const errorData = error.response.data as ErrorResponse;
                throw new Error(errorData.error || '请求失败');
            }
            throw error;
        }
    });
};

// 获取单个记录
export const fetchRecordById = async (id: string): Promise<Record> => {
    return retryRequest(async () => {
        try {
            const response = await api.get(`/records/${id}`);
            return response.data;
        } catch (error) {
            if (axios.isAxiosError(error)) {
                if (!error.response) {
                    throw new Error('无法连接到服务器，请检查服务器是否启动');
                }
                const errorData = error.response.data as ErrorResponse;
                throw new Error(errorData.error || '请求失败');
            }
            throw error;
        }
    });
};

// 创建新记录
export const createRecord = async (record: Omit<Record, 'id' | 'createTime' | 'lastDiagnosisTime'>): Promise<Record> => {
    try {
        const response = await api.post('/records', record);
        return response.data;
    } catch (error) {
        handleError(error as AxiosError);
        throw error;
    }
};

// 更新记录
export const updateRecord = async (id: string, record: Partial<Record>): Promise<Record> => {
    try {
        const response = await api.put(`/records/${id}`, record);
        return response.data;
    } catch (error) {
        handleError(error as AxiosError);
        throw error;
    }
};

// 上传图片
export const uploadImages = async (files: File[]): Promise<string[]> => {
    try {
        const formData = new FormData();
        files.forEach(file => {
            formData.append('images', file);
        });

        const response = await api.post('/upload', formData, {
            headers: {
                'Content-Type': 'multipart/form-data'
            }
        });

        return response.data.urls;
    } catch (error) {
        handleError(error as AxiosError);
        throw error;
    }
};

// 创建带有诊断记录的新病例
export const createRecordWithDiagnosis = async (
    record: Omit<Record, 'id' | 'createTime' | 'lastDiagnosisTime'>,
    files?: File[]
): Promise<Record> => {
    try {
        let imageUrls: string[] = [];
        if (files && files.length > 0) {
            imageUrls = await uploadImages(files);
        }

        const recordWithImages = {
            ...record,
            diagnosisRecords: record.diagnosisRecords.map((diagnosis, index) => ({
                ...diagnosis,
                images: diagnosis.images || [],
                ...(index === record.diagnosisRecords.length - 1 ? { images: imageUrls } : {})
            }))
        };

        return await createRecord(recordWithImages);
    } catch (error) {
        handleError(error as AxiosError);
        throw error;
    }
};

// 添加诊断记录
export const addDiagnosisToRecord = async (
    recordId: string,
    diagnosisContent: string,
    files?: File[]
): Promise<Record> => {
    try {
        const record = await fetchRecordById(recordId);
        let imageUrls: string[] = [];

        if (files && files.length > 0) {
            imageUrls = await uploadImages(files);
        }

        const updatedRecord = {
            ...record,
            diagnosisRecords: [
                ...record.diagnosisRecords,
                {
                    content: diagnosisContent,
                    diagnosisTime: new Date().toISOString(),
                    images: imageUrls
                }
            ]
        };

        return await updateRecord(recordId, updatedRecord);
    } catch (error) {
        handleError(error as AxiosError);
        throw error;
    }
};
