import React from 'react';

// 定义组件的属性接口
interface RecordDetailsProps {
    record: {
        id: string; // 病例的唯一标识符
        name: string; // 病例名称
        condition: string; // 病症名称
        contact: string; // 联系方式
        gender: string; // 性别
        age: number; // 年龄
        diagnosis: string; // 诊断记录
        lastDiagnosisDate: string; // 最后诊断时间
    };
    onUpdate: (updatedRecord: any) => void; // 更新病例信息的回调函数
}

// 定义 RecordDetails 组件
const RecordDetails: React.FC<RecordDetailsProps> = ({ record, onUpdate }) => {
    // 使用 React 的 useState 钩子来管理诊断记录的状态
    const [diagnosis, setDiagnosis] = React.useState(record.diagnosis);

    // 处理更新操作，将修改后的诊断记录传递给父组件
    const handleUpdate = () => {
        onUpdate({ ...record, diagnosis }); // 将更新后的诊断记录与原始记录合并
    };

    return (
        <div>
            {/* 显示病例详细信息 */}
            <h2>病例详细信息</h2>
            <p><strong>病例名称:</strong> {record.name}</p> {/* 显示病例名称 */}
            <p><strong>病症名称:</strong> {record.condition}</p> {/* 显示病症名称 */}
            <p><strong>联系方式:</strong> {record.contact}</p> {/* 显示联系方式 */}
            <p><strong>性别:</strong> {record.gender}</p> {/* 显示性别 */}
            <p><strong>年龄:</strong> {record.age}</p> {/* 显示年龄 */}
            <p><strong>诊断记录:</strong> {record.diagnosis}</p> {/* 显示诊断记录 */}
            <p><strong>最后诊断时间:</strong> {record.lastDiagnosisDate}</p> {/* 显示最后诊断时间 */}

            {/* 文本区域用于修改诊断记录 */}
            <textarea 
                value={diagnosis} 
                onChange={(e) => setDiagnosis(e.target.value)} // 更新诊断记录的状态
                placeholder="修改诊断记录" // 提示用户输入
            />

            {/* 按钮用于保存修改后的诊断记录 */}
            <button onClick={handleUpdate}>保存</button>
        </div>
    );
};

export default RecordDetails;
